import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { useNavigate } from "react-router-dom";
import './Signup.css';

export default function Signup() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [repeatPassword, setRepeatPassword] = useState("");
    const [email, setEmail] = useState("");
    const navigate = useNavigate();

    function validateForm() {
        if (password === repeatPassword) {
            if (!document.getElementById("error").innerHTML == null&&document.getElementById("error").innerHTML === 'Passwords do not match!') {
                document.getElementById("error").innerHTML = '';
            }
            return username.length > 0 && password.length > 0 && email.length > 0;
        } else if (password.length > 0 && repeatPassword.length > 0) {
            document.getElementById("error").innerHTML = 'Passwords do not match!';
            return false;
        }
    }

    function handleSubmit(event) {
        fetch('http://localhost:3001/signup', {
            method: 'POST',
            headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
            body: JSON.stringify({
                'username': username,
                'password': password,
                'email': email
            })
        }).then((response) => response.json()
        ).then((res) => {
            if (res === -1) {
                //Please try again message
                document.getElementById("error").innerHTML = 'Username already exists';
            } else if (res === -2) {
                document.getElementById("error").innerHTML = 'Account already created with this email!';
            } else {
                //If signup success, redirect with ?id=[id]
                navigate('/search', { 'state': { 'id': res } });
            }
        });
        event.preventDefault();
    }
    return (
        <div className="Signup">
            <h1>Sign up</h1>
            <Form onSubmit={handleSubmit}>
                <Form.Group size="lg" controlId="username">
                    <Form.Label>Username</Form.Label>
                    <Form.Control
                        autoFocus
                        type="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                </Form.Group>
                <Form.Group size="lg" controlId="password">
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </Form.Group>
                <Form.Group size="lg" controlId="repeatPassword">
                    <Form.Label>Repeat Password</Form.Label>
                    <Form.Control
                        type="password"
                        value={repeatPassword}
                        onChange={(e) => setRepeatPassword(e.target.value)}
                        />
                </Form.Group>
                <Form.Group size="lg" controlId="email">
                    <Form.Label>Email</Form.Label>
                    <Form.Control
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </Form.Group>
                <Button block size="lg" type="submit" disabled={!validateForm()}>
                    Login
                </Button>
                <div id="error" className="error"></div>
            </Form>
        </div>
    );
}
