import React from 'react';
import './Business.css';

class Business extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            listNames: {},
            key: this.props.key
        }
        //Set in_list and not_list's dropdown menu values to 'names'
        fetch('http://localhost:3001/listnames', {
            method: 'POST',
            headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
            body: JSON.stringify({
                'id': this.props.business.account_id
            })
        }).then((response) => response.json()
        ).then((names) => {
            //Format: {'list_name': 'list_id', ...}
            this.setState({ listNames: names });
        });
        this.addToList = this.addToList.bind(this);
        //this.openAddToListModal = this.openAddToListModal.bind(this);
    }
    


    submitAdd(business) {
        let listName = document.getElementById('listMenu' + business.id).options[document.getElementById('listMenu' + business.id).selectedIndex].text;
        console.log(document.getElementById('listMenu' + business.id).value);
        if (listName !== document.getElementById('listMenu' + business.id).value) {

            //Open success message modal
            document.getElementById("successModal" + business.id).style.display = 'block';
            document.getElementById("successMessage" + business.id).innerHTML = business.name + ' on ' + business.location.address1 + ' added to ' + listName + '!'
        }
    }

    closeModal(key) {
        document.getElementById("successModal"+key).style.display = 'none';
    }

    renderModalOptions() {
        return Object.keys(this.state.listNames).map(name => {
            return (
                <option value={this.state.listNames[name]}>{name}</option>
            );
        });
    }

    addToList() {
        console.log(this.state.business.name);
    }

    renderAddToList() {

    }

    // Renders the individual businesses returned in the search results
    render() {
        return (
            <div className="Business">
                <div className="image-container">
                    <a href={this.props.business.url} target="_blank">
                        <img src={this.props.business.image_url} alt='' width="280" height="280" />
                    </a>
                </div>
                <a href={this.props.business.url} target="_blank">
                    <h2>{this.props.business.name}</h2>
                </a>
                <div className="Business-information">
                    <div className="Business-address">
                        <p>{this.props.business.location.address1}</p>
                        <p>{this.props.business.location.city}</p>
                        <p>{`${this.props.business.location.state} ${this.props.business.location.zip_code}`}</p>
                    </div>
                    <div className="Business-reviews">
                        <h3>{this.props.business.categories[0].title}</h3>
                        <h3 className="rating">{`${this.props.business.rating} stars`}</h3>
                        <p>{`${this.props.business.review_count} reviews`}</p>
                    </div>
                </div>
                <div>
                    <select id={"listMenu"+this.props.business.id}>
                        {this.renderModalOptions()}
                        <option value="">Add to list...</option>
                    </select>
                    <button onClick={()=>this.submitAdd(this.props.business)}>Add to list</button>
                </div>
                <div id={"successModal"+this.props.business.id} class="modal">
                    <div class="modal-content">
                        <span class="close" onClick={() =>this.closeModal(this.props.business.id)}>&times;</span>
                        <h1 id={"successMessage"+this.props.business.id}>Location added to list!</h1>
                    </div>
                </div>
            </div>
        );
    }
}

export default Business;
