import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";


export default function Signup() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [repeatPassword, setRepeatPassword] = useState("");
    const [email, seetEmail] = useState("");
    const navigate = useNavigate();

    function validateForm() {
        if (password === repeatPassword && email) {
            return username.length > 0 && password.length > 0;
        }
    }

    function handleSubmit(event) {
        //Prevent sign up if email exists
    }
    return (
        <body>
            <h1>Sign up</h1>
            <div className="Signup">
                <Form onSubmit={handleSubmit}>
                    <Form.Group size="lg" controlId="username">
                        <Form.Label>Username</Form.Label>
                        <Form.Control
                            autoFocus
                            type="username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group size="lg" controlId="password">
                        <Form.Label>Password</Form.Label>
                        <Form.Control
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group size="lg" controlId="repeatPassword">
                        <Form.Label>Repeaet Password</Form.Label>
                        <Form.Control
                            type="pPassword"
                            value={repeatPassword}
                            onChange={(e) => setRepeatPassword(e.target.value)}
                            />
                    </Form.Group>
                    <Form.Group size="lg" controlId="email">
                        <Form.Label>Repeat Password</Form.Label>
                        <Form.Control
                            type="email"
                            value={email}
                            onChange={(e) => setRepeatPassword(e.target.value)}
                        />
                    </Form.Group>
                    <Button block size="lg" type="submit" disabled={!validateForm()}>
                        Login
                    </Button>
                    <Button block size="lg" onClick={() => { navigate('/signup') }}>
                        SignUp
                    </Button>

                </Form>
            </div>
        </body>
    );
}
